const axios = require("axios");

export async function fetchData(
  url,
  config = { method: "GET", data: null, headers: {} }
) {
  try {
    const response = await axios({
      method: config.method,
      url: url,
      data: config.data,
      headers: config.headers,
    });

    return response.data;
  } catch (error) {
    console.error("Error fetching data:", error);
    throw error;
  }
}

// Add the following function to include the access token in the headers
export function fetchDataWithToken(
  url,
  token,
  config = { method: "GET", data: null, headers: {} }
) {
  // Include the token in the headers
  const headersWithToken = {
    ...config.headers,
    Authorization: `Bearer ${token}`,
  };

  // Call the original fetchData function with updated headers
  return fetchData(url, { ...config, headers: headersWithToken });
}
