import Cookies from "js-cookie";

const axios = require("axios");

export async function fetchData(
  url,
  method = "GET",
  data = null,
  headers = {}
) {
  try {
    // Đọc giá trị của accessToken từ cookie
    const accessToken = Cookies.get("accessToken");

    // Kiểm tra xem accessToken có tồn tại không
    if (!accessToken) {
      console.error("accessToken not found in cookies");
      return;
    }

    // Thêm accessToken vào header
    headers.Authorization = `Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJob2FuZy5tYW5oLmtodWF0QGdtYWlsLmNvbSIsImlhdCI6MTcwMzc0MTUzOSwiZXhwIjoxNzAzODI3OTM5fQ.flb1HlBx6hQwMrw6wv6Y_nU91H9qnUFgFbaX-TxpXdE`;

    const response = await axios({
      method: method,
      url: url,
      data: data,
      headers: headers,
    });

    return response.data;
  } catch (error) {
    console.error("Error fetching data:", error);
    throw error;
  }
}
